import Greet from "./Greet";
import "./App.css";
import Message from "./Message";

function App() {
  const persons = [
    {
      name: "Fulya",
      surName: "Türe",
    },
    {
      name: "Yunus",
      surName: "Türe",
    },
    {
      name: "Zeynep Begüm",
      surName: "Türe",
    },
  ];
  return (
    <div className="App">
      <Message />
      {persons.map((person) => (
        <Greet name={person.name} surName={person.surName} />
      ))}
    </div>
  );
}

export default App;
